<template>    <h3 class="text-base font-bold mt-[15px]">        <span>风格选择</span>    </h3>    <el-select v-model="value" placeholder="请选择风格" class="w-full mt-[10px]">        <el-option            v-for="(item) in styleList"            :key="item.value"            :label="item.label"            :value="item.value"        />    </el-select></template><script setup lang="ts">import { useVModels } from '@vueuse/core'const emit = defineEmits<{    (event: 'update:modelValue', value: string): void}>()const props = withDefaults(    defineProps<{        modelValue?: any    }>(),    {        modelValue: {            version: '',            style: 'default'        }    })const { modelValue: value } = useVModels(props, emit)const styleList: any = [    {        value: 'vivid',        label: '生动'    },    {        value: 'natural',        label: '自然'    }]value.value = 'vivid'</script>