<template>    <div class="flex items-center cursor-pointer mt-[15px] text-base">        <div            class="flex items-center text-[#8e8e8e] hover:text-primary select-none"            @click="getDrawingExample"        >            <el-icon size="16" color="inherit"><Refresh /></el-icon>            <span class="ml-[4px]">随便试试:</span>        </div>        <div class="flex truncate w-[230px]">            <el-tooltip                class="ml-[10px]"                effect="light"                v-for="(item, index) in exampleLists"                :key="index"                :content="item.prompt"                placement="bottom"            >                <span class="ml-[10px]" @click="appendKeywords(item.prompt_en)">                    {{ item.prompt }}                </span>            </el-tooltip>        </div>    </div></template><script setup lang="ts">import { Refresh } from '@element-plus/icons-vue'import { drawingExample } from '~/api/drawing'import { useVModels } from '@vueuse/core'const emit = defineEmits<{    (event: 'update:modelValue', value: string): void}>()const props = withDefaults(    defineProps<{        modelValue?: any    }>(),    {        modelValue: ''    })const { modelValue: value } = useVModels(props, emit)// 示例列表const exampleLists = ref<any[]>([])// 获取示例列表const getDrawingExample = async () => {    try {        exampleLists.value = await drawingExample()    } catch (error) {        console.log('请求绘画示例失败=>', error)    }}// 添加关键词const appendKeywords = (content: string) => {    emit('update:modelValue', content)}getDrawingExample()</script>