<template>    <el-tabs        v-show="drawModel.is_open"        v-model="drawModel.current.model"        @tab-click="handleClick($event)"        class="generation-picker"    >        <el-tab-pane            v-for="(item, index) in drawModel.modelList"            :key="index"            :label="item.name"            :name="item.model"        ></el-tab-pane>    </el-tabs></template><script setup lang="ts">import {drawingModel} from '~/api/drawing'import {useUserStore} from "~/stores/user";const emit = defineEmits<{    (event: 'update:modelValue', value: string): void    (event: 'update:model', value: any): void}>()const props = withDefaults(    defineProps<{        balance?: any        modelValue?: any    }>(),    {        balance: 0,        modelValue: ''    })const userStore = useUserStore()// 绘画模型const drawModel = reactive({    is_open: 0,    current: {        balance: 1,        name: '',        model: ''    } as any,    modelList: [] as any[]})watch(    () => props.modelValue,    (value) => {        const data = drawModel.modelList.find((item: any) => {            return item.model === value        })        drawModel.current = data    },    {immediate: false})// 获取绘画模型数据const getDrawModel = async () => {    try {        const { is_open, lists } = await drawingModel()        drawModel.is_open = is_open        drawModel.modelList = lists        drawModel.current = lists.find((item: any) => item.default) || lists[0]        emit('update:model', drawModel.current)        emit('update:modelValue', drawModel.current.model)    } catch (error) {        console.log('获取绘画模型失败=>', error)    }}const handleClick = (event: any) => {    drawModel.current = drawModel.modelList[event.index]    emit('update:model', drawModel.current)    emit('update:modelValue', drawModel.current.model)}watch(    () => userStore?.userInfo?.is_draw_limit,    () => {        getDrawModel()    })getDrawModel()</script><style lang="scss" scoped>.generation-picker {    :deep(.el-tabs__nav-wrap.is-scrollable) {        padding: 0 30px;    }    :deep(.el-tabs__item) {        padding: 0 10px;        font-size: var(--el-font-size-medium);    }}</style>