<template>    <h3 class="text-base font-bold mt-[15px]">        <span>模型选择</span>    </h3>    <!--    模型    -->    <div class="flex mt-[5px]">        <div            v-for="item in modelList"            :key="item.name"            class="model-item model-item_model"            :class="{                'model-item_active': currentModel === item.value            }"            @click="changeVersion(item.value)"        >            <div                class="model-item-bg"                :style="{                    'background-image': `url(${item.background})`                }"            >                <div class="h-[35px] text-center leading-[40px]"><!--                    <el-icon--><!--                        v-if="currentModel === item.value"--><!--                        color="#ffffff"--><!--                        size="22"--><!--                    >--><!--                        <CircleCheck />--><!--                    </el-icon>-->                </div>                <div                    class="text-base w-full text-center h-[45px] leading-[60px]"                    style="background: linear-gradient(180deg,transparent,#000);"                >                    {{ item.name }}                </div>            </div>        </div>    </div>    <h3 class="text-base font-bold mt-[15px]">        <span>风格选择</span>    </h3>    <!--    风格选择    -->    <div class="mt-[5px]">        <div            v-for="(item, index) in data[currentModel]"            :key="index"            class="model-item model-item_style !w-[156px] !h-[115px] inline-block"            :class="{                'model-item_active': value.engine === item.engine && value.style === item.value            }"            @click="handleStyleSelection(item.engine, item.value)"        >            <div                class="model-item-bg"                :style="{                    'background-image': `url(${item.poster})`                }"            >                <div class="h-[60px] flex justify-center items-center"><!--                    <el-icon--><!--                        v-if="value.engine === item.engine"--><!--                        color="#ffffff"--><!--                        size="26"--><!--                    >--><!--                        <CircleCheck />--><!--                    </el-icon>-->                </div>                <div                    class="text-sm w-full text-center h-[50px] leading-[65px]"                    style="background: linear-gradient(180deg,transparent,#000);"                >                    {{ item.text }}                </div>            </div>        </div>    </div></template><script setup lang="ts">import { useAppStore } from '~/stores/app'import { useVModels } from '@vueuse/core'import { CircleCheck } from '@element-plus/icons-vue'import YkTy from '~/assets/images/drawing/yj-ty.png'import YjDm from '~/assets/images/drawing/yj-dm.png'import YjXs from '~/assets/images/drawing/yj-xs.png'const appStore = useAppStore()const emit = defineEmits<{    (event: 'update:modelValue', value: string): void}>()const props = withDefaults(    defineProps<{        data: any        modelValue?: any    }>(),    {        data: {            cartoon: [],            common: [],            reality: []        },        modelValue: {            engine: '',            style: 'default'        }    })const { modelValue: value } = useVModels(props, emit)const currentModel = ref<string>('common')const modelList: any = [    {        name: '通用模型',        value: 'common',        background: YkTy    },    {        name: '动漫模型',        value: 'cartoon',        background: YjDm    },    {        name: '现实模型',        value: 'reality',        background: YjXs    }]const changeVersion = (val: string) => {    currentModel.value = val    const data = props.data[val][0]    handleStyleSelection(data.engine, data.value)}const handleStyleSelection = (engine: string, style: string) => {    value.value.engine = engine    value.value.style = style}// changeVersion('common')handleStyleSelection('default_dreamer_diffusion', '')</script><style scoped>.model-item {    cursor: pointer;    width: 102px;    height: 64px;    margin-right: 6px;    padding: 1px 2px;    border: 2px solid #e5e5e5;    color: #ffffff;    border-radius: 6px;    overflow: hidden;    box-sizing: border-box;}.model-item_model:last-child {    margin-right: 0px;}.model-item_style:nth-child(2n) {    margin-right: 0px;}.model-item_active {    border: 2px solid var(--el-color-primary);}.model-item-bg {    display: flex;    flex-direction: column;    justify-content: center;    align-items: center;    background-size: cover;    width: 100%;    height: 100%;    overflow: hidden;    border-radius: 6px;}.model-item-bg span:first-child {    font-weight: 500;}</style>