<template>    <l-textarea        v-model="value"        class="mt-[10px]"        maxlength="1024"        :rows="4"        :show-word-limit="false"        :customStyle="{            height: '100px',            paddingBottom: '24px'        }"        placeholder="输入关键词，支持中英文，参数放最后～"    >        <template #length-suffix>            <el-button                v-if="example.status"                class="length-suffix"                type="primary"                :link="true"                @click="value = example.content"            >                试试示例            </el-button>        </template>    </l-textarea></template><script setup lang="ts">import { useVModels } from '@vueuse/core'const emit = defineEmits<{    (event: 'update:modelValue', value: string): void}>()const props = withDefaults(    defineProps<{        modelValue?: any        example: any    }>(),    {        modelValue: '',        example: {            content: '',            status        }    })const { modelValue: value } = useVModels(props, emit)</script><style scoped>.length-suffix {    position: absolute;    bottom: 0;    left: 38%;    font-size: 14px;    width: 100%;    height: 30px;    padding-right: 10px;    background: #ffffff;}</style>