<template>    <h3 class="text-base font-bold mt-[15px]">        <span>模型选择</span>    </h3>    <!--    模型    -->    <div class="mt-[5px]">        <div            v-for="item in modelList"            :key="item.name"            class="model-item"            :class="{                'model-item_active': value == item.id            }"            @click="value = item.id"        >            <div                class="model-item-bg"                :style="{                    'background-image': `url(${item.preview_img})`                }"            >            </div>            <div class="model-item-text">{{ item.name }}</div>        </div>    </div></template><script setup lang="ts">import { useVModels } from '@vueuse/core'const emit = defineEmits<{    (event: 'update:modelValue', value: string): void}>()const props = withDefaults(    defineProps<{        modelValue?: any        modelList?: any    }>(),    {        modelValue: '',        modelList: []    })const { modelValue: value } = useVModels(props, emit)watchEffect(() => {    const modelList = props.modelList    if (modelList.length !== 0) {        value.value = modelList[0].id    }})</script><style scoped>.model-item {    display: inline-block;    position: relative;    cursor: pointer;    width: 100px;    height: 64px;    padding: 3px;    margin-right: 10px;    border: 2px solid #e5e5e5;    color: #ffffff;    border-radius: 4px;}.model-item:nth-child(3n) {    margin-right: 0px;}.model-item_active {    border: 2px solid var(--el-color-primary);}.model-item-bg {    background-size: cover;    width: 100%;    height: 100%;}.model-item-text {    position: absolute;    bottom: 0;    left: 0;    display: flex;    justify-content: center;    align-items: center;    margin: 3px;    width: 90px;    height: 24px;    border-radius: 4px;    background-color: rgba(0, 0, 0, 0.5);}</style>