<template>    <div class="mt-[15px]">        <div class="text-base">二维码风格</div>        <div class="style-container overflow-hidden">            <div                v-for="(item, index) in template"                :key="index"                class="float-left"                :class="{                    'style-active': item.id == value                }"                @click="value = item.id"            >                <el-image                    class="w-full h-[100px]"                    :src="item.preview_img"                    fit="cover"                    alt="风格模版"                />                <div class="py-[3px] text-center">                    {{ item.name }}                </div>            </div>        </div>    </div></template><script setup lang="ts">import { useVModels } from '@vueuse/core'const emit = defineEmits<{    (event: 'update:modelValue', value: string): void}>()const props = withDefaults(    defineProps<{        template?: any        modelValue?: any    }>(),    {        template: [],        modelValue: ''    })const { modelValue: value } = useVModels(props, emit)watchEffect(() => {    const template = props.template    if (template.length !== 0) {        value.value = template[0].id    }})</script><style scoped>.style-container>div {    cursor: pointer;    margin-top: 10px;    margin-right: 10px;    border-radius: 4px;    width: 100px;    padding: 8px;    border: 2px solid #e5e5e5;}.style-container>div:nth-child(3n) {    margin-right: 0px;}.style-container .style-active {    border: 2px solid var(--el-color-primary);}</style>