<template>    <div class="mt-[15px]">        <h1 class="text-base font-bold">码点形状</h1>        <div class="flex flex-wrap mt-[10px]">            <div                v-for="(item, index) in template"                :key="index"                class="pixel cursor-pointer flex justify-center items-center"                :class="{                    'pixel-active': value == item.value                }"                @click="value = item.value"            >                <img class="w-[20px] h-[20px]" :src="item.preview_img" />                <span class="ml-[8px]">{{ item.name }} </span>            </div>        </div>    </div></template><script setup lang="ts">import { useVModels } from '@vueuse/core'const emit = defineEmits<{    (event: 'update:modelValue', value: string): void}>()const props = withDefaults(    defineProps<{        template: any        modelValue?: any    }>(),    {        template: [],        modelValue: 'random'    })const { modelValue: value } = useVModels(props, emit)</script><style lang="scss" scoped>.pixel {    transition: all 0.5s;    user-select: none;    display: flex;    justify-content: center;    align-items: center;    margin-right: 10px;    width: 80px;    height: 40px;    border-radius: 4px;    border: 1px solid #dcdfe6;}.pixel:hover {    border: 1px solid var(--el-color-primary);    background-color: var(--el-color-primary-light-9);}.pixel-active {    border: 1px solid var(--el-color-primary);}</style>