<template>    <div class="text-base mt-[15px]">        <span class="mr-2">二维码明显程度</span>        <span class="text-sm text-[#999]">            (数值越大二维码越容易扫描)        </span>    </div>    <div class="flex items-center mt-[10px] pl-[8px]">        <el-slider            style="--el-slider-button-size: 14px"            :min="0"            :max="1"            :step="0.01"            v-model="value"        />        <el-input            class="ml-4"            type="number"            style="width: 100px"            v-model="value"            step="0.01"        ></el-input>    </div></template><script setup lang="ts">import { useVModels } from '@vueuse/core'const emit = defineEmits<{    (event: 'update:modelValue', value: string): void}>()const props = withDefaults(    defineProps<{        modelValue?: any    }>(),    {        modelValue: ''    })const { modelValue: value } = useVModels(props, emit)</script>