<template>    <!--    上传图片    -->    <upload        class="w-full mt-[15px]"        :show-progress="true"        :data="{            type: 'draw'        }"        :disabled="!userStore.isLogin"        @click="handleClick"        @success="(val) => (value = val.data.uri)"    >        <div            class="flex flex-col items-center justify-center upload-view-box"        >            <template v-if="!value">                <el-icon :size="40"><upload-filled /></el-icon>                <div class="mt-[6px]">上传图片</div>            </template>            <template v-else>                <el-image                    class="w-full h-[90px]"                    :src="value"                    fit="contain"                />                <div class="upload-view-item">                    <el-icon                        size="24"                        class="upload-clear"                        @click.stop="value = ''"                    >                        <CircleCloseFilled />                    </el-icon>                    <el-button type="primary">重新上传</el-button>                </div>            </template>        </div>    </upload></template><script setup lang="ts">import { useVModels } from '@vueuse/core'import Upload from '~/components/upload/index.vue'import { CircleCloseFilled, UploadFilled } from '@element-plus/icons-vue'import { useUserStore } from '~/stores/user'const emit = defineEmits<{    (event: 'update:modelValue', value: string): void}>()const props = withDefaults(    defineProps<{        modelValue?: any    }>(),    {        modelValue: ''    })const { modelValue: value } = useVModels(props, emit)const userStore = useUserStore()const handleClick = () => {    if (!userStore.isLogin) return userStore.toggleShowLogin(true)}</script><style lang="scss" scoped>// 上传参考图.upload-view-box {    position: relative;    width: 100%;    height: 102px;    border-right: 4px;    border: 1px dashed #dcdfe6;    .upload-view-item {        display: none;        position: absolute;        top: 0;        left: 0;        width: 100%;        height: 100%;        border-radius: 4px;        background-color: rgba(0, 0, 0, 0.3);    }    .upload-clear {        position: absolute;        right: 20px;        top: 14px;        cursor: pointer;    }}.upload-view-box:hover {    color: var(--el-color-primary);    border-color: var(--el-color-primary);}.upload-view-box:hover .upload-view-item {    display: flex;    align-items: center;    justify-content: center;}</style>