<template>    <div class="mt-[15px]">        <div class="text-base">模版风格</div>        <div class="style-container overflow-hidden">            <div                v-for="(item, index) in template"                :key="index"                class="float-left"                :class="{                    'style-active': item.value == value                }"                @click="handleClick(item.value)"            ><!--                <el-image--><!--                    class="w-full h-[100px]"--><!--                    :src="item.preview_img"--><!--                    fit="cover"--><!--                    alt="风格模版"--><!--                />-->                <div class="py-[3px] text-xs text-center">                    {{ item.name }}                </div>            </div>        </div>    </div></template><script setup lang="ts">import { useVModels } from '@vueuse/core'const emit = defineEmits<{    (event: 'update:modelValue', value: string): void    (event: 'update:way', value: number): void}>()const props = withDefaults(    defineProps<{        template?: any        modelValue?: any        way: any    }>(),    {        template: [],        modelValue: '',        way: 1    })const { modelValue: value } = useVModels(props, emit)const handleClick = (val: string) => {    if (val != value.value) {        value.value = val        emit('update:way', 2)    } else {        value.value = ''        emit('update:way', 1)    }}</script><style scoped>.style-container>div {    cursor: pointer;    margin-top: 10px;    margin-right: 10px;    border-radius: 4px;    width: 100px;    padding: 8px;    border: 1px solid #e5e5e5;}.style-container>div:nth-child(3n) {    margin-right: 0px;}.style-container .style-active {    border: 1px solid var(--el-color-primary);}</style>