<template>    <h3 class="text-base font-bold mt-[20px]">        <span>生成方式</span>    </h3>    <div class="mt-[10px] text-sm">        <!--    类型选择    -->        <div class="inline-flex cursor-pointer bg-[#f0f2f2] rounded-[14px] text-[#333333]">            <div                v-for="item in typeArr"                :key="item.value"                class="rounded-[14px] w-[72px] h-[28px] leading-[28px] flex justify-center"                :class="{                    'bg-primary text-btn-text': item.value === value                }"                @click="value = item.value"            >                {{ item.label }}            </div>        </div>        <!--    模版    -->        <div v-show="value === 2">            <slot name="mode"></slot>        </div>        <!--    自定义    -->        <div v-show="value === 1">            <slot name="custom"></slot>        </div>    </div></template><script setup lang="ts">import { useVModels } from '@vueuse/core'const emit = defineEmits<{    (event: 'update:modelValue', value: number): void}>()const props = withDefaults(    defineProps<{        modelValue?: any    }>(),    {        modelValue: 2    })const { modelValue: value } = useVModels(props, emit)const typeArr = [    {        label: '模版',        value: 2    },    {        label: '自定义',        value: 1    }]</script>