<template>    <h3 class="text-base font-bold mt-[12px]">        <span>生成内容</span>        <span class="text-error">*</span>    </h3>    <div class="mt-[10px] text-sm">        <!--    类型选择    -->        <div class="inline-flex cursor-pointer bg-[#f0f2f2] rounded-[14px] text-[#333333]">            <div                v-for="item in typeArr"                :key="item.value"                class="rounded-[14px] w-[72px] h-[28px] leading-[28px] flex justify-center"                :class="{                    'bg-primary text-btn-text': item.value === value.type                }"                @click="value.type = item.value"            >                {{ item.label }}            </div>        </div>        <!--    图片模式-上传二维码    -->        <upload v-if="value.type == 2" v-model="value.qr_image" @click="handleClick"></upload>        <!--    文字模式-输入内容    -->        <l-textarea            class="mt-[15px]"            v-if="value.type == 1"            v-model="value.qr_content"            maxlength="100"            :rows="4"            placeholder="请输入二维码内容, 或者已备案域名或合法的文本内容"            @click="handleClick"        ></l-textarea>    </div></template><script setup lang="ts">import Upload from './components/upload.vue'import LTextarea from '~/components/l-textarea/index.vue'import { useVModels } from '@vueuse/core'import { useUserStore } from '~/stores/user'const emit = defineEmits<{    (event: 'update:modelValue', value: string): void}>()const props = withDefaults(    defineProps<{        modelValue?: any    }>(),    {        modelValue: {}    })const { modelValue: value } = useVModels(props, emit)const { isLogin, toggleShowLogin } = useUserStore()const typeArr = [    {        label: '图片模式',        value: 2    },    {        label: '文字模式',        value: 1    }]const handleClick = () => {    if (!isLogin) {        toggleShowLogin()    }}</script>