<script lang="ts" setup>import ImageError from '~/assets/images/drawing/error.png'import IconDelete from '~/assets/images/drawing/icon_delete.png'const deleteQrcode = inject<(options: number[]) => void>('deleteQrcode')const props = withDefaults(    defineProps<{        value?: any        id: number    }>(),    {        value: '',        id: 0    })</script><template>    <div class="fail-box">        <!--    头部操作    -->        <div class="flex justify-between items-center">            <div class="fail-box__tag">绘画失败</div>            <div class="flex items-center">                <el-tooltip effect="dark" content="删除" placement="top">                    <img                        class="w-[16px] h-[16px] ml-[16px] cursor-pointer"                        :src="IconDelete"                        alt="删除"                        @click="deleteQrcode([id])"                    />                </el-tooltip>            </div>        </div>        <el-image class="w-[200px] h-[200px] mt-[50px]" :src="ImageError" />        <div class="text-xl mt-[20px] w-full">绘画失败</div>        <el-popover            placement="top"            title=""            effect="dark"            :width="300"            trigger="hover"            :content="value"        >            <template #reference>                <div                    class="mt-[10px] text-base text-[#999999] px-[20px] h-[90px] line-clamp-5"                >                    错误信息：{{ value }}                </div>            </template>        </el-popover>    </div></template><style lang="scss" scoped>.fail-box {    display: inline-block;    width: 100%;    height: 100%;    padding: 15px 20px;    text-align: center;    &__tag {        padding: 4px 8px;        font-size: 14px;        border-radius: 4px;        color: #f63210;        background: #ffefeb;    }}</style>